properties.groovyx-script-body:
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

try {
    // Read flowfile content
    def text = session.read(flowFile).withStream { stream -> 
        new String(stream.readAllBytes(), 'UTF-8')
    }

    // Parse JSON
    def jsonSlurper = new JsonSlurper()
    def data = jsonSlurper.parseText(text)

    // Validate required fields
    def requiredFields = ['device_id', 'patient_id', 'glucose_level', 'alert_level', 'timestamp']
    def missingFields = requiredFields.findAll { !data.containsKey(it) }

    if (missingFields) {
        session.transfer(flowFile, REL_FAILURE)
        session.putAttribute(flowFile, 'validation.error', "Missing required fields: ${missingFields.join(', ')}")
        return
    }

    // Validate data types and ranges
    def errors = []

    // Glucose level validation
    try {
        def glucose = data.glucose_level.toFloat()
        if (glucose < 20 || glucose > 600) {
            errors << "Glucose level out of range: ${glucose}"
        }
    } catch (e) {
        errors << "Invalid glucose level format: ${data.glucose_level}"
    }

    // Alert level validation
    def validAlertLevels = ['normal', 'low', 'high', 'critical_low', 'critical_high']
    if (!validAlertLevels.contains(data.alert_level)) {
        errors << "Invalid alert level: ${data.alert_level}"
    }

    if (errors) {
        session.transfer(flowFile, REL_FAILURE)
        session.putAttribute(flowFile, 'validation.errors', errors.join('; '))
        return
    }

    // Add validation success attribute
    session.putAttribute(flowFile, 'validation.status', 'success')
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    session.transfer(flowFile, REL_FAILURE)
    session.putAttribute(flowFile, 'validation.error', "JSON parsing error: ${e.message}")
}

